import React, { Component } from 'react';
import { pickTheme } from '../redux/actions'
import { connect } from 'react-redux';
import { ThemeProvider } from 'styled-components/native';
import { Container, HeaderContainer, HeaderText, Text } from '../theming/masterStyle'
import { TextInput } from 'react-native';


class CreateProfile extends Component {
    state = {
        bio: '',
      }
  render() {
      console.log(this.props.theme)
    return (
        <ThemeProvider theme={ this.props.theme }>

            <Container>
                <HeaderContainer>
                    <HeaderText>create your profile.</HeaderText>
                </HeaderContainer>
                <Text>wait! tell us a little about yourself first ...</Text>
                <TextInput 
                style={{ height: 40, borderColor: 'gray', borderWidth: 1 }}
                    value={this.state.bio} 
                    maxLength={250} />
                    <Text>hello</Text>
            </Container>
        </ThemeProvider>
    );
  }
}

function mapStateToProps(state) {
    return {
        theme: state.themeReducer.theme
    }
}

export default connect(mapStateToProps, {pickTheme})(CreateProfile);
