import React, { Component } from 'react';
import { pickTheme } from '../redux/actions'
import { pinkTheme, lavenderTheme, oliveTheme } from '../theming/themes'
import { connect } from 'react-redux';
import { ThemeProvider } from 'styled-components/native';
import { List } from 'react-native-paper'
import { Container, HeaderContainer, HeaderText, Text, Button, ButtonText } from '../theming/masterStyle'
import {Collapse,CollapseHeader, CollapseBody, AccordionList} from 'accordion-collapse-react-native';



class Settings extends Component {
    state = {
        expanded: false
    }

    expand() {
        this.setState({
            expanded: !this.state.expanded
        })
    }


    render() {
        console.log(this.props.theme)
    return (
        <ThemeProvider theme={ this.props.theme }>

            <Container>
                <HeaderContainer>
                    <HeaderText>settings</HeaderText>
                </HeaderContainer>
                <Button onPress={() => this.props.navigation.navigate('SignUp')}>
                    <ButtonText>go to SignUp ...</ButtonText>
                </Button>
                <Text>pick a color theme!</Text>
                <List.Accordion
                    title="themes!"
                    description="themes!"
                    left={props => <List.Icon {...props} color={this.props.theme.PRIMARY_COLOR} icon="folder"></List.Icon>}
                    expanded={this.state.expanded}
                    onPress={() => {this.expand()}}>
                    <List.Item onPress={() => this.props.pickTheme(pinkTheme)} title="Pink Theme" />
                    <List.Item onPress={() => this.props.pickTheme(lavenderTheme)} title="Lavender Theme" />
                    <List.Item onPress={() => this.props.pickTheme(oliveTheme)} title="Olive Theme" />
                </List.Accordion>

                <Collapse>
                    <CollapseHeader>
                        <Text>Pick a theme!</Text>
                    </CollapseHeader>
                    <CollapseBody>
                    <Text>Olive Theme</Text>
                    <Text>Lavender Theme</Text>
                    <Text onPress={() => this.props.pickTheme(pinkTheme)}>Pink Theme</Text>
                    </CollapseBody>
                </Collapse>

<AccordionList
        list={this.state.list}
        header={this._head}
        body={this._body}
        keyExtractor={item => item.key}
      />
            </Container>
        </ThemeProvider>
    );
  }
}

function mapStateToProps(state) {
    return {
        theme: state.themeReducer.theme
    }
}

export default connect(mapStateToProps, {pickTheme})(Settings);
