import React, { Component } from 'react';
import { pickTheme } from '../redux/actions'
import { connect } from 'react-redux';
import { ThemeProvider } from 'styled-components/native';
import { Subtitle, HeaderContainer, HeaderText, Button, ButtonText } from '../theming/masterStyle'
import { Rating } from 'react-native-elements';
import { BioInput, FormArea, CreateProfileContent, FormInput, ErrorText } from '../theming/createStyle'
import { Formik } from 'formik'
import * as yup from 'yup';

// form validation
const SignUpSchema = yup.object({
    user: yup.string().required('required!!!'),
    password: yup.string().required('required!!!')
})
const HEART_IMAGE = require('../mockData/heart-thin.jpeg')
class CreateProfile extends Component {
    state = {
        bio: '',
      }


  render() {
      console.log(this.props.theme)
    return (
        <ThemeProvider theme={ this.props.theme }>

            <CreateProfileContent>   


            <Rating
            type='custom'
            ratingImage={HEART_IMAGE}
            ratingColor='#3498db'
            ratingBackgroundColor='#c8c7c8'
            ratingCount={10}
            imageSize={30}
            onFinishRating={this.ratingCompleted}
            style={{ paddingVertical: 10 }}
            />   
                        <Formik 
                            initialValues={{ user: '', password: ''}}
                            validationSchema={SignUpSchema}
                            onSubmit={(values) => {
                                console.log(values)
                            }}
                        >
                            {/* get access to props of Formik */}
                            {(props) => (
                                <FormArea>
                                    <HeaderContainer>
                                        <HeaderText>real quick..</HeaderText>
                                        <Subtitle>tell us a bit about yourself! create your profile.
                                        </Subtitle>
                                    </HeaderContainer>
                                    <BioInput 
                                        placeholder='please enter a short bio! (250 words max)' 
                                        onChangeText={props.handleChange('user')} 
                                        maxLength={250}
                                        multiline
                                        numberOfLines={7}
                                        value = {props.values.user}
                                    />
                                    <ErrorText>{props.touched.user && props.errors.user }</ErrorText>
                                    <FormInput 
                                        placeholder='UARK password' 
                                        onChangeText={props.handleChange('password')} 
                                        value = {props.values.password}
                                        secureTextEntry
                                    />
                                    <ErrorText>{props.touched.password && props.errors.password }</ErrorText>
                                    <Button title="Submit" onPress={() => props.handleSubmit()}>
                                        <ButtonText>get started!</ButtonText>
                                    </Button>
                                </FormArea>
                            )}
                        </Formik>

                </CreateProfileContent>

        </ThemeProvider>
    );
  }
}

function mapStateToProps(state) {
    return {
        theme: state.themeReducer.theme
    }
}

export default connect(mapStateToProps, {pickTheme})(CreateProfile);
