import React, { Component } from 'react';
import { pickTheme } from '../redux/actions'
import { pinkTheme, lavenderTheme, oliveTheme } from '../theming/themes'
import { connect } from 'react-redux';
import { ThemeProvider } from 'styled-components/native';
import { List } from 'react-native-paper'
import { Container, HeaderContainer, HeaderText, Text, Button, ButtonText } from '../theming/masterStyle'


class Settings extends Component {
    state = {
        expanded: false
    }

    expand() {
        this.setState({
            expanded: !this.state.expanded
        })
    }

    render() {
        console.log(this.props.theme)
    return (
        <ThemeProvider theme={ this.props.theme }>

            <Container>
                <HeaderContainer>
                    <HeaderText>settings</HeaderText>
                </HeaderContainer>
                <Text>pick a color theme!</Text>
                    <Button onPress={() => this.props.pickTheme(pinkTheme)}>
                        <ButtonText>pink theme</ButtonText>
                    </Button>

                    <Button onPress={() => this.props.pickTheme(lavenderTheme)}>
                        <ButtonText>lavender theme</ButtonText>
                    </Button>

                    <Button onPress={() => this.props.pickTheme(oliveTheme)}>
                        <ButtonText>olive theme</ButtonText>
                    </Button>

                    <Button onPress={() => this.props.navigation.navigate('SignUp')}>
                        <ButtonText>go to SignUp ...</ButtonText>
                    </Button>
                    <List.Accordion
                        title="themes!"
                        description="themes!"
                        left={props => <List.Icon {...props} icon="folder"></List.Icon>}
                        expanded={this.state.expanded}
                        onPress={() => {this.expand()}}>
                        <List.Item title="First item" />
                        <List.Item title="Second item" />
                    </List.Accordion>
            </Container>
        </ThemeProvider>
    );
  }
}

function mapStateToProps(state) {
    return {
        theme: state.themeReducer.theme
    }
}

export default connect(mapStateToProps, {pickTheme})(Settings);
