import React, { Component } from 'react';
import { pickTheme } from '../../redux/actions'
import { connect } from 'react-redux';
import { ThemeProvider } from 'styled-components/native';
import AsyncStorage from '@react-native-community/async-storage'
import Modal from 'react-native-modal';
import ProfileModal from './ProfileModal'
import ProfileCard from './ProfileCard'
import { ModalContainer } from '../../theming/settingStyle'
import { AllUsersList } from '../../theming/exploreStyle'
import { HeaderText, Subtitle, Container, Text, HeaderContainer } from '../../theming/masterStyle'



class Explore extends Component {
    constructor(props) {
        super(props);
        this.closeModal = this.closeModal.bind(this)
        this.setModalVisible = this.setModalVisible.bind(this)
      }

    state = {
        allUsers: [],
        modalContent: '',
        modalVisible: false
    }

    getAllUsers() {
        console.log("in get user data")
        var data;
        let apiEndpoint = "http://mobile-app.ddns.uark.edu/CRUDapis/users/getAllUsers";
        console.log(apiEndpoint)
        // call api endpoint, sending in user to add to db
        fetch(apiEndpoint,)
            .then((response) => response.text())
            .then((json) => {
                // parse the response & extract data
                data = JSON.parse(json)
                if (data.isError === false) {
                    console.log(data)
                    this.setState({ allUsers: data.result })
                }
        })
        .catch((error) => console.error(error))
        .finally(() => {
            if (this.state.userLoaded === true) {
            console.log("we got set the data!")
            }
            // if successful addition to db, navigate to create profile
            console.log("finally block") 
        })
    }

    setModalVisible(visible, modalType) {
        console.log("set modal visible")
        this.setState({ 
          modalVisible: visible,
          modalContent: modalType });
      }

      closeModal() {
        console.log("closing modal")
        this.setState({ 
          modalVisible: !this.state.modalVisible });
      }


      async componentDidMount() {
        console.log("in component did mount")
        this.getAllUsers()
      }

  render() {

    const allUsers = this.state.allUsers.map((user) => {
        return (
          <ProfileCard name={user.userFNAME}/>
        )
    })

      console.log(this.props.theme)
      let modalDisplay;
        switch(this.state.modalContent) {
          case "ProfileModal":
            modalDisplay = <ProfileModal 
            closeModal={this.closeModal} />
            break;

        default:
            break;
        }

    return (
        <ThemeProvider theme={ this.props.theme }>

            <Container>
                <HeaderContainer>
                    <HeaderText>explore</HeaderText>
                    <Subtitle>boop</Subtitle>
                </HeaderContainer>

                <AllUsersList>
                    {console.log("ALL USERS LIST")}
                    {console.log(this.state.allUsers)}
                   {this.state.allUsers.map((user) => {
                       return (
                        <ProfileCard 
                            setModalVisible={this.setModalVisible}
                            fname={user.userFNAME}
                            lname={user.userLNAME}
                            major={user.userMAJOR}
                            classification={user.userGRADE_LEVEL}
                            profilePic={user.userPROFILEPIC}/>
                       )
                    })}
                </AllUsersList>

                <Modal isVisible={this.state.modalVisible}>
                    <ModalContainer>
                        {modalDisplay}
                    </ModalContainer>
                </Modal>
            </Container>
        </ThemeProvider>
    );
  }
}

function mapStateToProps(state) {
    return {
        theme: state.themeReducer.theme
    }
}

export default connect(mapStateToProps, {pickTheme})(Explore);
