import React from 'react';
import { useSelector } from 'react-redux';
import { Button, ButtonText } from '../../theming/masterStyle'
import { ModalView, Title } from '../../theming/settingStyle'
import { CardContainer, ProfileImage, InfoSection } from '../../theming/exploreStyle'
import LottieView from 'lottie-react-native';
import { Text, Subtitle } from '../../theming/masterStyle'
import { MaterialCommunityIcons } from '@expo/vector-icons'



const ProfileCard = (props) => {
    console.log(props)
    console.log(useSelector)
    const primary = useSelector(state => state.themeReducer.theme.PRIMARY_COLOR);
    return (
        <CardContainer>
            <ProfileImage  
            source={{uri: props.profilePic}} />

            <InfoSection>
                <Title onPress={() => { props.setModalVisible(true, "ProfileModal"); }}>{props.fname} {props.lname}</Title>
                <Subtitle>{props.classification}</Subtitle>
                <Subtitle>{props.major}</Subtitle>
            </InfoSection>

            <MaterialCommunityIcons 
                name="heart"
                color={ primary}
                size={41} />

        </CardContainer>

    )
}

export default ProfileCard
